import { StatusBar } from 'expo-status-bar'
import React, { useEffect, useState } from 'react'
import { SafeAreaView, Platform, Animated } from 'react-native'
import { Frame } from './Frame'

const Sprite = ({ sequence = [0, 1, 2, 3], fps = 24 }) => {
  const [frameArr, setFrameArr] = useState(sequence)
  let incrementFrame = () => {
    setFrameArr((currentSequence) => {
      let prevFrame = currentSequence.shift()
      currentSequence.push(prevFrame)
      return currentSequence
    })
  }
  useEffect(() => {
    let intervalID = setInterval(() => {
      console.log(frameArr)
      incrementFrame()
    }, 1000)
    return () => clearInterval(intervalID)
  }, [])
  return (
    <Frame
      frame={frameArr.shift()}
      height={260}
      width={400}
      cols={4}
      rows={3}
      source={require('./assets/bunny-hop-spritesheet-1.png')}
    />
  )
}
export default function App() {
  return (
    <SafeAreaView
      style={{
        flex: 1,
        backgroundColor: '#f00',
        paddingTop: Platform.OS === 'android' ? 30 : 0,
      }}
    >
      <Sprite fps={5} />
    </SafeAreaView>
  )
}
