import { StatusBar } from 'expo-status-bar'
import React, { useEffect, useState } from 'react'
import { SafeAreaView, Platform, Animated } from 'react-native'
import { Frame } from './Frame'

const Sprite = ({ sequence = [0, 1, 2, 3], fps = 24 }) => {
  const [frame, setFrame] = useState(sequence[0])
  let incrementFrame = () => {
    setFrame((currentFrame) => {
      if (currentFrame > sequence.length - 1) {
        return 0
      }
      return currentFrame + 1
    })
  }
  setInterval(() => {
    incrementFrame()
  }, 1000 / fps)

  return (
    <Frame
      frame={frame}
      height={260}
      width={400}
      cols={4}
      rows={3}
      source={require('./assets/bunny-hop-spritesheet-1.png')}
    />
  )
}
export default function App() {
  return (
    <SafeAreaView
      style={{
        flex: 1,
        backgroundColor: '#f00',
        paddingTop: Platform.OS === 'android' ? 30 : 0,
      }}
    >
      <Sprite fps={5} />
    </SafeAreaView>
  )
}
