import { StatusBar } from 'expo-status-bar'
import React, { useEffect, useState } from 'react'
import { SafeAreaView, View, Platform, Image } from 'react-native'

const Frame = ({
  frame = 0,
  height = 260,
  width = 400,
  cols = 3,
  rows = 2,
}) => {
  const frameWidth = width / cols,
    frameHeight = height / rows
  // let column = frame % cols
  // let row = Math.ceil(frame / rows)
  return (
    <View
      style={{ overflow: 'hidden', width: frameWidth, height: frameHeight }}
    >
      <Image
        style={{
          height,
          width,
          // right: frameWidth * column,
          // bottom: frameHeight * row,
        }}
        source={require('./assets/bunny-hop-spritesheet-1.png')}
      />
    </View>
  )
}

export default function App() {
  return (
    <SafeAreaView
      style={{
        flex: 1,
        backgroundColor: '#f00',
        paddingTop: Platform.OS === 'android' ? 30 : 0,
      }}
    >
      <Frame />
    </SafeAreaView>
  )
}
