import { StatusBar } from 'expo-status-bar'
import React, { useEffect, useState } from 'react'
import { SafeAreaView, View, Platform, Image } from 'react-native'

const Frame = ({
  frame = 1,
  height = 260,
  width = 400,
  cols = 4,
  rows = 3,
}) => {
  const frameWidth = width / cols,
    frameHeight = height / cols
  let column = 4
  let row = 0
  return (
    <View>
      <View
        style={{
          backgroundColor: '#00FF00',
          overflow: 'hidden',
          width: frameWidth,
          height: frameHeight,
        }}
      >
        <Image
          style={{
            height,
            width,
            right: frameWidth * column,
            bottom: frameHeight * row,
          }}
          source={require('./assets/bunny-hop-spritesheet-1.png')}
        />
      </View>
    </View>
  )
}

export default function App() {
  return (
    <SafeAreaView
      style={{
        flex: 1,
        backgroundColor: '#f00',
        paddingTop: Platform.OS === 'android' ? 30 : 0,
      }}
    >
      <Frame />
    </SafeAreaView>
  )
}
