import React, { useState } from 'react'
import { Frame } from './Frame'

const Sprite = ({ sequence = [0, 1, 2, 3], fps = 24 }) => {
  const [frameArr, setFrameArr] = useState(sequence)
  const [frame, setFrame] = useState(sequence[0])
  let incrementFrame = () => {
    setFrameArr((currentSequence) => {
      let prevFrame = currentSequence.shift()
      currentSequence.push(prevFrame)
      setFrame(currentSequence[0])
      return currentSequence
    })
  }
  useEffect(() => {
    let intervalID = setInterval(() => {
      incrementFrame()
    }, 1000 / fps)
    return () => clearInterval(intervalID)
  }, [])
  return (
    <Frame
      frame={frame}
      height={260}
      width={400}
      cols={4}
      rows={3}
      source={require('./assets/bunny-hop-spritesheet-1.png')}
    />
  )
}

export default Sprite
