import React from 'react'
import { View, Image } from 'react-native'

export const Frame = ({
  source,
  frame = 2,
  height = 260,
  width = 400,
  cols = 4,
  rows = 3,
}) => {
  const frameWidth = width / cols,
    frameHeight = height / cols
  let column = frame % (cols - 1)
  let row = Math.floor(frame / (cols - 1))

  return (
    <View>
      <View
        style={{
          backgroundColor: '#00FF00',
          overflow: 'hidden',
          width: frameWidth,
          height: frameHeight,
        }}
      >
        <Image
          style={{
            height,
            width,
            right: frameWidth * column,
            bottom: frameHeight * row,
          }}
          source={source}
        />
      </View>
    </View>
  )
}
